    body = None
    headers = {}
    if method == 'POST' and param:
        body = urlencode(param).encode('utf-8')
        headers['Content-Type'] = 'application/x-www-form-urlencoded'
        headers['Content-Length'] = str(len(body))
    if use_proxy:
        headers['Host'] = host

    try:
        if use_proxy:
            logger.debug("Sending %s request to %s with body: %s", method, full_url, body)
            conn.request(method, full_url, body=body, headers=headers)
        else:
            logger.debug("Sending %s request to %s with body: %s", method, path, body)
            conn.request(method, path, body=body, headers=headers)

        response = conn.getresponse()
        logger.debug("Received response: %s %s", response.status, response.reason)

        response_body = response.read()
        if sys.version_info[0] >= 3:
            response_body = response_body.decode('utf-8')

        if 200 <= response.status < 300:
            return response_body
        else:
            logger.error("HTTP error %s: %s", response.status, response.reason)
            raise Exception("HTTP error {}: {}".format(response.status, response.reason))
    except Exception as e:
        logger.exception("Request failed: %s", e)
        raise
    finally:
        conn.close()
        logger.debug("Connection closed")
