        def index():
            return render_template('index.html')

        @self.app.route('/results', methods=['POST'])
        def results():
            text = request.form['review']
            label, prob = self.classify(text)
            return render_template('results.html', text=text, label=label, prob=prob)

        @self.app.route('/feedback', methods=['POST'])
        def feedback():
            review_id = request.form['id']
            correct_label = int(request.form['sentiment'])
            review = self.db.session.get(Review, review_id)
            if review:
                self.train(review.document, correct_label)
                review.sentiment = correct_label
                self.db.session.commit()
            return redirect(url_for('index'))

    def _load_model(self, path):
        with open(path, 'rb') as f:
            return pickle.load(f)

    def classify(self, document):
        prob = self.clf.predict_proba([document])[0]
        label = self.clf.classes_[prob.argmax()]
        return label, prob.max()

    def train(self, document, y):
        self.clf.partial_fit([document], [y], classes=[0, 1])

    def init_db(self):
        class Review(self.db.Model):
            __tablename__ = 'reviews'
            id = self.db.Column(self.db.Integer, primary_key=True)
            document = self.db.Column(self.db.Text)
            sentiment = self.db.Column(self.db.Integer)

        self.Review = Review

    def sqlite_entry(self, document, y):
        entry = self.Review(document=document, sentiment=y)
        self.db.session.add(entry)
        self.db.session.commit()

    def run(self, debug=True):
        self.app.run(debug=debug)

