from typing import Optional, Dict, Any

class ExchangeRates(db.Model):
    __tablename__ = 'exchange_rates'
    date = db.Column(db.Date, primary_key=True)
    rates = db.Column(db.JSONB, default={})

class ExchangeRateAPI:
    def __init__(self):
        self.app = Sanic(__name__)
        self.db = Gino()
        self.config = {'DATABASE_URL': os.getenv('DATABASE_URL')}
        self.db.init_app(self.app)
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_route(self.exchange_rates, "/rates", methods=["GET"])
        self.app.add_route(self.exchange_rates, "/rates/<date>", methods=["GET"])

    async def exchange_rates(self, request: Request, date: Optional[str] = None) -> response.HTTPResponse:
        try:
            if date:
                query_date = datetime.strptime(date, "%Y-%m-%d").date()
            else:
                latest_record = await ExchangeRates.query.order_by(ExchangeRates.date.desc()).gino.first()
                if not latest_record:
                    return response.json({"error": "No exchange rate data available"}, status=404)
                query_date = latest_record.date

            record = await ExchangeRates.get(query_date)
            if not record:
                return response.json({"error": "No exchange rate data for this date"}, status=404)

            base_currency = request.args.get("base", "EUR").upper()
            if base_currency not in record.rates:
                return response.json({"error": f"Base currency {base_currency} not available"}, status=400)

            target_symbols = request.args.get("symbols", "").upper().split(",") if "symbols" in request.args else None
            target_symbols = [s.strip() for s in target_symbols if s.strip()]

            base_rate = record.rates.get(base_currency, 1.0)
            converted_rates = {curr: rate / base_rate for curr, rate in record.rates.items()}

            if target_symbols:
                filtered_rates = {curr: converted_rates[curr] for curr in target_symbols if curr in converted_rates}
            else:
                filtered_rates = converted_rates

            return response.json({
                "base": base_currency,
                "date": query_date.isoformat(),
                "rates": filtered_rates
            })

        except ValueError:
            return response.json({"error": "Invalid date format. Use YYYY-MM-DD"}, status=400)
        except Exception as e:
            return response.json({"error": str(e)}, status=500)

    def run(self, host: str = "0.0.0.0", port: int = 8000) -> None:
        self.app.run(host=host, port=port)

