        self.graphiql = graphiql
        self.graphql_ide = graphql_ide or "graphiql"
        self.allow_queries_via_get = allow_queries_via_get
        self.result_override = result_override
        self.multipart_uploads_enabled = multipart_uploads_enabled

        @self.app.route("/graphql", methods=["GET", "POST"])
        async def graphql_route(request: Request) -> response.BaseHTTPResponse:
            return response.json({"data": "mock_response"})

    def _graphql_request(
        self,
        method: Literal["get", "post"],
        query: Optional[str],
        variables: Optional[Dict[str, object]] = None,
        files: Optional[Dict[str, BytesIO]] = None,
        headers: Optional[Dict[str, str]] = None,
        **kwargs: Any,
    ) -> TestClient:
        url = "/graphql"
        if method.lower() == "get":
            if not self.allow_queries_via_get:
                raise ValueError("GraphQL queries via GET are not allowed")
            params = {"query": query}
            if variables:
                params["variables"] = json.dumps(variables)
            return self.request(url, "GET", headers=headers, params=params)
        else:
            if files and self.multipart_uploads_enabled:
                boundary = "----WebKitFormBoundary7MA4YWxkTrZu0gW"
                body = BytesIO()
                body.write(f"--{boundary}\r\n".encode())
                body.write(b"Content-Disposition: form-data; name=\"operations\"\r\n\r\n")
                operations = {"query": query, "variables": variables}
                body.write(json.dumps(operations).encode())
                body.write(b"\r\n")
                body.write(f"--{boundary}\r\n".encode())
                body.write(b"Content-Disposition: form-data; name=\"map\"\r\n\r\n")
                body.write(json.dumps({"0": ["variables.file"]}).encode())
                body.write(b"\r\n")
                for name, file in files.items():
                    body.write(f"--{boundary}\r\n".encode())
                    body.write(f'Content-Disposition: form-data; name="{name}"; filename="testfile"\r\n'.encode())
                    body.write(b"Content-Type: application/octet-stream\r\n\r\n")
                    body.write(file.getvalue())
                    body.write(b"\r\n")
                body.write(f"--{boundary}--\r\n".encode())
                headers = headers or {}
                headers["Content-Type"] = f"multipart/form-data; boundary={boundary}"
                return self.request(url, "POST", headers=headers, data=body.getvalue())
            else:
                json_body = {"query": query, "variables": variables}
                return self.request(url, "POST", headers=headers, json=json_body)

    def request(
        self,
        url: str,
        method: Literal["get", "post", "patch", "put", "delete"],
        headers: Optional[Dict[str, str]] = None,
        **kwargs: Any,
    ) -> TestClient:
        with self.app.test_client() as client:
            if method.lower() == "get":
                resp = client.get(url, headers=headers, **kwargs)
            elif method.lower() == "post":
                resp = client.post(url, headers=headers, **kwargs)
            elif method.lower() == "patch":
                resp = client.patch(url, headers=headers, **kwargs)
            elif method.lower() == "put":
                resp = client.put(url, headers=headers, **kwargs)
            elif method.lower() == "delete":
                resp = client.delete(url, headers=headers, **kwargs)
            else:
                raise ValueError(f"Unsupported method: {method}")
            return resp

    def get(self, url: str, headers: Optional[Dict[str, str]] = None) -> TestClient:
        return self.request(url, "GET", headers=headers)

    def post(
        self,
        url: str,
        data: Optional[bytes] = None,
        json: Optional[Dict] = None,
        headers: Optional[Dict[str, str]] = None,
    ) -> TestClient:
        return self.request(url, "POST", headers=headers, data=data, json=json)

