        self.twe = TopicalWordEmbeddingsWrapper(self.model_dir)
        self.lda_topic_words = self._read_topic_words_from_file()
        self._setup_routes()

    def _read_topic_words_from_file(self, topic_words_file_name: str = 'topic_words.lda.txt') -> defaultdict:
        topic_words_file = os.path.join(self.model_dir, topic_words_file_name)
        topic_words = defaultdict(list)
        with open(topic_words_file, 'r') as f:
            for line in f:
                parts = line.strip().split('\t')
                if len(parts) < 2:
                    continue
                topic_id = int(parts[0])
                word, score = parts[1].split()
                score = float(score)
                topic_words[topic_id].append((word, score))
        return topic_words

    def _get_param(self, request, param_name, default_value=None, is_list=False):
        if is_list:
            return request.args.getlist(param_name, default_value)
        return request.args.get(param_name, default_value)

    def _strip_to_none(self, text: str) -> Optional[str]:
        if text is None:
            return None
        stripped = text.strip()
        return stripped if stripped else None

    def _response(self, success: bool = True, data=None, message=None) -> HTTPResponse:
        return response.json({
            'success': success,
            'data': data,
            'message': message
        })

    def _error_response(self, message: str = 'Invalid request') -> HTTPResponse:
        return self._response(success=False, message=message)

    def _setup_routes(self):
        @self.app.post('/tokenize')
        async def tokenize(request):
            text = request.json.get('text') or request.args.get('text')
            text = self._strip_to_none(text)
            if text is None:
                return self._error_response('Text is required')
            tokens = text.split()
