import http.server
from http.server import BaseHTTPRequestHandler, HTTPServer
import socketserver
import threading
from collections import ChainMap
import gzip
import mimetypes
import os
import io
from contextlib import contextmanager

class HTTPTestHandler(BaseHTTPRequestHandler):
    def __init__(self, *args, **kwargs):
        self.static_files = self.server.static_files
        self.dynamic_files = self.server.dynamic_files
        self.files = ChainMap(self.dynamic_files, self.static_files)
        super().__init__(*args, **kwargs)

    def _respond(self, code=200, headers=None, data=b""):
        self.send_response(code)
        if headers:
            for k, v in headers.items():
                self.send_header(k, v)
        self.send_header('Content-Length', len(data))
        self.end_headers()
        self.wfile.write(data)

    def do_GET(self):
        if self.path in self.files:
            content = self.files[self.path]
            content_type, _ = mimetypes.guess_type(self.path)
            content_type = content_type or 'application/octet-stream'
            headers = {'Content-Type': content_type}
            if 'Range' in self.headers:
                range_header = self.headers['Range']
                start = end = total = len(content)
                unit, ranges = range_header.split('=')
                range_parts = ranges.split('-')
                start = int(range_parts[0])
                if len(range_parts) > 1:
                    end = int(range_parts[1])
                if start >= total:
                    self.send_error(416)
                    return
                if end >= total:
                    end = total - 1
                content = content[start:end+1]
                headers['Content-Range'] = f'bytes {start}-{end}/{total}'
                self._respond(code=206, headers=headers, data=content)
            else:
                accept_encoding = self.headers.get('Accept-Encoding', '')
                if 'gzip' in accept_encoding:
                    out = io.BytesIO()
                    with gzip.GzipFile(fileobj=out, mode='wb') as f:
                        f.write(content)
                    compressed = out.getvalue()
                    headers['Content-Encoding'] = 'gzip'
                    self._respond(headers=headers, data=compressed)
                else:
                    self._respond(headers=headers, data=content)
        else:
            self.send_error(404)

    def do_POST(self):
        content_length = int(self.headers.get('Content-Length', 0))
        transfer_encoding = self.headers.get('Transfer-Encoding', '').lower()
        if transfer_encoding == 'chunked':
            data = b''.join(self.read_chunks())
        else:
            data = self.rfile.read(content_length)
        self.dynamic_files[self.path] = data
        self._respond(code=201, headers={'Location': self.path}, data=b'Created')

    def read_chunks(self):
        while True:
            line = self.rfile.readline()
            if not line:
                break
            line = line.strip()
            if not line:
                continue
            try:
                chunk_size = int(line, 16)
            except ValueError:
                break
            if chunk_size == 0:
                self.rfile.readline()
                break
            chunk_data = self.rfile.read(chunk_size)
            self.rfile.readline()
            yield chunk_data

@contextmanager
def serve():
    server_address = ('localhost', 0)
    httpd = HTTPServer(server_address, HTTPTestHandler)
    httpd.static_files = {
        '/index.html': b'<html><body><h1>Hello, World!</h1></body></html>',
        '/data.txt': b'This is a static text file.\n'
    }
    httpd.dynamic_files = {}
    def run_server():
        httpd.serve_forever()
    thread = threading.Thread(target=run_server, daemon=True)
    thread.start()
    port = httpd.server_port
    url = f'http://localhost:{port}'
    try:
        yield url
    finally:
        httpd.shutdown()
        thread.join()
