import json
import random
import re
import urllib.parse
import http.client
import ssl

class JSONRPCExecutor:
    def __init__(self, command, url, jsonrpc_method, jsonrpc_params=None, status="^2\\d\\d$", result_validator=None, verify_tls=True, method="POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params or []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method
        parsed_url = urllib.parse.urlparse(url)
        self.path = parsed_url.path
        if parsed_url.query:
            self.path += '?' + parsed_url.query

    def _send_request(self, conn):
        self.request_id = random.randint(1, 1000000)
        payload = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": self.request_id
        }
        body = json.dumps(payload)
        headers = {
            'Content-Type': 'application/json',
            'Content-Length': str(len(body))
        }
        conn.request(self.method, self.path, body, headers)

    def _validate_response(self, response):
        data = response.read().decode('utf-8')
        try:
            json_response = json.loads(data)
        except json.JSONDecodeError:
            return False
        if json_response.get('jsonrpc') != '2.0':
            return False
        if json_response.get('id') != self.request_id:
            return False
        if 'error' in json_response:
            return False
        if 'result' not in json_response:
            return False
        self.result = json_response.get('result')
        return True

    def execute(self):
        parsed_url = urllib.parse.urlparse(self.url)
        host = parsed_url.hostname
        port = parsed_url.port
        if not port:
            if parsed_url.scheme == 'https':
                port = 443
            else:
                port = 80
        if parsed_url.scheme == 'https':
            if self.verify_tls:
                context = ssl.create_default_context()
            else:
                context = ssl._create_unverified_context()
            conn = http.client.HTTPSConnection(host, port, context=context)
        else:
            conn = http.client.HTTPConnection(host, port)
        try:
            self._send_request(conn)
            response = conn.getresponse()
            if not re.match(self.status, str(response.status)):
                return False
            if not self._validate_response(response):
                return False
            if self.result_validator is not None:
                is_valid, _ = self.result_validator(self.result)
                if not is_valid:
                    return False
            return True
        finally:
            conn.close()

