            'gzip': ['zcat'],
            'bzip2': ['bzcat'],
            'lz4': ['lz4cat', '-d'],
            'xz': ['xzcat'],
            'none': ['cat'],
        }
        if compression not in compression_map:
            return {'filesystem': '<unknown>'}
        decompress_cmd = compression_map[compression]
        cat_cmd = ['cat'] + abs_partclone_image_list
        partclone_cmd = ['partclone.info']
        try:
            cat_process = subprocess.Popen(cat_cmd, stdout=subprocess.PIPE)
            decompress_process = subprocess.Popen(decompress_cmd, stdin=cat_process.stdout, stdout=subprocess.PIPE)
            partclone_process = subprocess.Popen(partclone_cmd, stdin=decompress_process.stdout, stdout=subprocess.PIPE, stderr=subprocess.DEVNULL, text=True)
            stdout, _ = partclone_process.communicate()
            if partclone_process.returncode != 0:
                return {'filesystem': '<unknown>'}
            return Partclone.parse_partclone_info_output(stdout)
        except (FileNotFoundError, OSError):
            return {'filesystem': '<unknown>'}

    @staticmethod
    def parse_partclone_info_output(output):
        info_dict = {'filesystem': '<unknown>'}
        if not output:
            return info_dict
        for line in output.splitlines():
            if ':' in line:
                key, value = line.split(':', 1)
                key = key.strip()
                value = value.strip()
                info_dict[key] = value
        if 'Filesystem' in info_dict:
            info_dict['filesystem'] = info_dict['Filesystem'].lower()
            del info_dict['Filesystem']
