import urllib.parse
import json
import base64

class BaseProxy:
    DEFAULT_HTTP_TIMEOUT = 30

    def __init__(self, service_url=None, service_port=None, btc_conf_file=None, timeout=DEFAULT_HTTP_TIMEOUT, connection=None):
        self.__id_count = 0
        self.__auth_header = None
        if connection:
            self.__conn = connection
            self.__service_url = service_url or ""
            self.__url = urllib.parse.urlparse(self.__service_url)
        else:
            if not service_url:
                raise ValueError("Service URL must be provided")
            self.__service_url = service_url
            self.__url = urllib.parse.urlparse(self.__service_url)
            host = self.__url.hostname
            port = self.__url.port
            if not host:
                raise ValueError("Invalid service URL: missing hostname")
            if self.__url.scheme == 'https':
                self.__conn = http.client.HTTPSConnection(host, port, timeout=timeout)
            else:
                self.__conn = http.client.HTTPConnection(host, port, timeout=timeout)
        user = self.__url.username
        password = self.__url.password
        if btc_conf_file:
            with open(btc_conf_file, 'r') as f:
                for line in f:
                    line = line.strip()
                    if line.startswith('rpcuser='):
                        user = line.split('=', 1)[1]
                    elif line.startswith('rpcpassword='):
                        password = line.split('=', 1)[1]
        if user and password:
            credentials = base64.b64encode(f"{user}:{password}".encode('ascii')).decode('ascii')
            self.__auth_header = f"Basic {credentials}"

    def _call(self, service_name, *args):
        self.__id_count += 1
        payload = {
            "jsonrpc": "2.0",
            "method": service_name,
            "params": list(args),
            "id": self.__id_count
        }
        body = json.dumps(payload)
        headers = {
            'Content-Type': 'application/json',
            'Content-Length': str(len(body))
        }
        if self.__auth_header:
            headers['Authorization'] = self.__auth_header
        self.__conn.request("POST", self.__url.path, body, headers)
        return self._get_response()

    def _get_response(self):
        response = self.__conn.getresponse()
        status = response.status
        if status != 200:
            raise Exception(f"HTTP error {status}: {response.reason}")
        try:
            data = response.read().decode('utf-8')
            json_response = json.loads(data)
        except Exception as e:
            raise Exception("Failed to parse JSON response") from e
        if 'error' in json_response:
            error = json_response['error']
            message = error.get('message', 'Unknown error')
            code = error.get('code', 0)
            raise Exception(f"JSON-RPC error: {message} (code {code})")
        return json_response['result']

    def close(self):
        if hasattr(self, '__conn') and self.__conn:
            self.__conn.close()

    def __del__(self):
        self.close()
