import common

class Client:
    def __init__(self, host: str = common.DEFAULT_HOST, port: int = common.DEFAULT_PORT) -> None:
        self.host = host
        self.port = port
        self.pending_commands: List[common.Command] = []
        self.socket: Optional[socket.socket] = None
        self.client_id: Optional[str] = None
        self.current_custom_attributes: Dict[str, Any] = {}
        self.clients_attributes: Dict[str, Dict[str, Any]] = {}
        self.rooms_attributes: Dict[str, Dict[str, Any]] = {}
        self.current_room: Optional[str] = None
        self._recv_buffer: bytes = b''

    def __del__(self) -> None:
        self.disconnect()

    def connect(self) -> None:
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.connect((self.host, self.port))
        self._recv_buffer = b''

    def disconnect(self) -> None:
        if self.socket:
            self.socket.close()
            self.socket = None

    def is_connected(self) -> bool:
        return self.socket is not None

    def send_command(self, command: common.Command) -> bool:
        if not self.is_connected():
            return False
        self.pending_commands.append(command)
        return True

    def handle_connection_lost(self) -> None:
        self.disconnect()
        self.client_id = None
        self.current_room = None
        self.current_custom_attributes.clear()
        self.clients_attributes.clear()
        self.rooms_attributes.clear()

    def fetch_incoming_commands(self) -> List[common.Command]:
        commands = []
        if not self.is_connected():
            return commands
        try:
            while True:
                data = self.socket.recv(4096)
                if not data:
                    break
                self._recv_buffer += data
                while len(self._recv_buffer) >= 4:
                    length = int.from_bytes(self._recv_buffer[:4], 'big')
                    if len(self._recv_buffer) < 4 + length:
                        break
                    cmd_data = self._recv_buffer[4:4+length]
                    self._recv_buffer = self._recv_buffer[4+length:]
                    try:
                        commands.append(common.Command.from_bytes(cmd_data))
                    except:
                        pass
            self.handle_connection_lost()
        except (socket.error, socket.timeout):
            self.handle_connection_lost()
        return commands

    def fetch_outgoing_commands(self, commands_send_interval: int = 0) -> None:
        if not self.is_connected() or not self.pending_commands:
            return
        try:
            for cmd in self.pending_commands:
                cmd_bytes = cmd.to_bytes()
                self.socket.sendall(len(cmd_bytes).to_bytes(4, 'big') + cmd_bytes)
            self.pending_commands.clear()
        except (socket.error, socket.timeout):
            self.handle_connection_lost()

    def fetch_commands(self, commands_send_interval: int = 0) -> List[common.Command]:
        self.fetch_outgoing_commands(commands_send_interval)
        return self.fetch_incoming_commands()

