        self.proxies = proxies or {}
        self.bucket = bucket
        self.fh = None
        self.dccsock = None
        self.filesize = 0
        self.received = 0
        self.speeds = []
        self.sleep = 0.1
        self.last_recv_size = 0
        self.send_64bits_ack = True
        self.abort = False
        self.status_notify = None
        self.prev_time = time.time()

    def create_socket(self):
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.setsockopt(socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)
        return sock

    def _write_func(self, buf):
        if self.bucket:
            self.bucket.consume(len(buf))
        self.fh.write(buf)
        self.received += len(buf)
        self.last_recv_size = len(buf)
        current_time = time.time()
        time_diff = current_time - self.prev_time
        if time_diff > 0:
            speed = len(buf) / time_diff
            self.speeds.append(speed)
            if len(self.speeds) > 3:
                self.speeds.pop(0)
        self.prev_time = current_time

    def _send_ack(self):
        fmt = '!Q' if self.send_64bits_ack else '!I'
        ack_data = struct.pack(fmt, self.received)
        self.dccsock.sendall(ack_data)

