                self._write_func(data)
                self._send_ack()
                self.update_progress()
        finally:
            self.close()

    def abort_downloads(self):
        self.abort = True

    def update_progress(self):
        if self.status_notify:
            self.status_notify(self.received, self.filesize, self.speed, self.percent)

