    CMD_PEER_MSG = 2
    CMD_GROUP_MSG = 3
    CMD_ACK = 4
    CMD_SYNC_REQUEST = 5
    CMD_SYNC_RESPONSE = 6
    CMD_PING = 7
    CMD_PONG = 8

    def __init__(self):
        self.seq = 0
        self.sock = None
        self.sync_key = 0
        self.syncing = False
        self.sync_timestamp = 0
        self.next_sync_key = 0

    def connect_server(self, device_id, token, host=None, port=24430, use_ssl=True):
        try:
            self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self.sock.settimeout(10)
            self.sock.connect((host, port))
            if use_ssl:
                context = ssl.create_default_context()
                self.sock = context.wrap_socket(self.sock, server_hostname=host)
            auth_msg = {
                'cmd': self.CMD_AUTH,
                'device_id': device_id,
                'token': token
            }
            self._send_message(auth_msg)
            cmd, s, m = self.recv_message()
            if cmd == self.CMD_ACK:
                return True
            return False
        except Exception:
            self.close()
            return False

    def close(self):
        if self.sock:
            self.sock.close()
            self.sock = None

    def recv_message(self):
        while True:
            try:
                raw_length = self._recv_all(4)
                if not raw_length:
                    return None, None, None
                msg_length = struct.unpack('!I', raw_length)[0]
                data = self._recv_all(msg_length)
                if not data:
                    return None, None, None
                msg = json.loads(data.decode('utf-8'))
                cmd = msg.get('cmd')
                s = msg.get('s')
                m = msg.get('m')
                self.ack_message(s)
                self.handle_message(cmd, s, m)
                return cmd, s, m
            except socket.timeout:
                self.send_ping()
            except Exception:
                return None, None, None

    def _recv_all(self, n):
        data = b''
        while len(data) < n:
            packet = self.sock.recv(n - len(data))
            if not packet:
                return None
            data += packet
        return data

    def handle_message(self, cmd, s, m):
        if cmd == self.CMD_SYNC_RESPONSE:
            self.sync_key = m.get('sync_key')
            self.syncing = False
            self.sync_timestamp = time.time()

    def ack_message(self, s):
