            try:
                read_list = []
                write_list = []
                except_list = [self.sock] if self.sock else []
                if not self._connected and self.sock:
                    write_list.append(self.sock)
                else:
                    if self.sock:
                        read_list.append(self.sock)
                        if self._send_buffer:
                            write_list.append(self.sock)
                if not read_list and not write_list and not except_list:
                    break
                try:
                    readable, writable, exceptional = select.select(read_list, write_list, except_list, 1.0)
                except select.error as e:
                    self.logger.error(f"Select error: {e}")
                    self.sock.close()
                    break
                if exceptional and self.sock in exceptional:
                    self.logger.error("Socket exception occurred")
                    self.sock.close()
                    break
