        self.inet = {'TCP', 'UDP'}
        self.unix = {'UNIX_STREAM', 'UNIX_DGRAM'} if self.on_unix else set()

        if proto not in self.protocols:
            raise ValueError(f"Unsupported protocol: {proto}")
        family, sock_type = self.protocols[proto]

        if family == socket.AF_INET and version is not None:
            if version == 6:
                family = socket.AF_INET6
            elif version != 4:
                raise ValueError("Version must be 4 or 6")

        if family == socket.AF_UNIX:
            if not isinstance(connect_to, str):
                raise ValueError("Unix domain socket address must be a string")
        else:
            if not isinstance(connect_to, tuple) or len(connect_to) < 2:
                raise ValueError("Internet socket address must be a tuple (host, port)")

        self.connection = Connection(connect_to, proto, [])
        self.timeout = timeout
        self.sockopts = []
        self.setsockopt(**kwargs)
        self.connection_pool = queue.Queue(maxsize=pool) if pool > 0 else None

        if proto in self.streams:
            self.send_and_receive = self._stream_send_and_receive
        elif proto in self.datagrams:
            self.send_and_receive = self._dgram_send_and_receive
        else:
            raise ValueError(f"Protocol {proto} not supported for send_and_receive")

    def settimeout(self, timeout):
        self.timeout = timeout

    def setsockopt(self, *sockopts, **kwargs):
        for opt in sockopts:
            if isinstance(opt, tuple) and len(opt) == 3:
                self.sockopts.append(opt)
        for key, value in kwargs.items():
            optname = getattr(socket, key, None)
            if optname is None:
                raise ValueError(f"Unknown socket option: {key}")
            self.sockopts.append( (socket.SOL_SOCKET, optname, value) )

    def _register_socket(self):
        family, sock_type = self.protocols[self.connection.proto]
        sock = socket.socket(family, sock_type)
        for level, optname, value in self.sockopts:
            sock.setsockopt(level, optname, value)
        sock.settimeout(self.timeout)
        sock.connect(self.connection.address)
        return sock

    def _stream_send_and_receive(self, _socket, message, *args, **kwargs):
        _socket.sendall(message.encode())
        data = b''
        while True:
            try:
                chunk = _socket.recv(4096)
                if not chunk:
                    break
                data += chunk
            except socket.timeout:
                break
        return BytesIO(data)

    def _dgram_send_and_receive(self, _socket, message, buffer_size=4096, *args):
        _socket.send(message.encode())
        data, _ = _socket.recvfrom(buffer_size)
        return BytesIO(data)

    def request(self, message, timeout=False, *args, **kwargs):
        if self.connection_pool is not None:
            try:
                _socket = self.connection_pool.get_nowait()
            except queue.Empty:
                _socket = self._register_socket()
        else:
            _socket = self._register_socket()

        if timeout is not False:
            _socket.settimeout(timeout)

        try:
            response = self.send_and_receive(_socket, message, *args, **kwargs)
        except Exception as e:
            raise e
        finally:
            if self.connection_pool is not None:
                try:
                    self.connection_pool.put_nowait(_socket)
                except queue.Full:
                    _socket.close()
            else:
                _socket.close()

        return response
