        server_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        server_socket.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        server_socket.bind(('localhost', self.port))
        server_socket.listen(1)
        connection, _ = server_socket.accept()
        connection.settimeout(self.DEFAULT_TIMEOUT)
        server_socket.close()
        return connection

    def _send_as_json(self, dictionary: Dict[str, Any]) -> None:
        json_str = json.dumps(dictionary)
        self._send_string(json_str)

    def _get_json_dict(self) -> Dict[str, Any]:
        data = self._get_data()
        if data is None:
            raise ConnectionError("Failed to receive JSON data")
        return json.loads(data)

    def _get_data(self) -> Optional[str]:
        try:
            length_bytes = self.connection.recv(4)
        except socket.timeout:
            return None
        if not length_bytes:
            return None
        length = struct.unpack('>I', length_bytes)[0]
        data = b''
        remaining = length
        while remaining > 0:
            try:
                chunk = self.connection.recv(remaining)
            except socket.timeout:
                return None
            if not chunk:
                return None
            data += chunk
            remaining -= len(chunk)
        return data.decode('utf-8')

    def _send_string(self, string: str) -> None:
        data = string.encode('utf-8')
        length = len(data)
        self.connection.sendall(struct.pack('>I', length))
        self.connection.sendall(data)

    def close(self) -> None:
        if self.connection:
            self.connection.close()

