import os
import time

class TCPServer:
    def __init__(self, port):
        self.port = int(port)
        self.process = None
        self.is_running = False

    def start(self):
        args = [self.port]
        self.process = Process(target=self._subprocess_server_tcp, args=args)
        self.process.start()
        time.sleep(1)
        self.is_running = True

    def stop(self):
        try:
            os.kill(self.process.pid, 9)
        except OSError:
            self.process.terminate()
        finally:
            self.is_running = False

    def _subprocess_server_tcp(self, port):
        s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        s.bind(('localhost', port))
        s.listen(True)
        conn, addr = s.accept()

        while True:
            data = conn.recv(1024)
            conn.send(b"RECEIVED: " + bytes(data))

