            def handle(self):
                try:
                    msg_bytes = self.request.recv(4096)
                    if not msg_bytes:
                        return
                    import json
                    msg = json.loads(msg_bytes.decode('utf-8'))
                    handler_type = msg.get('type')
                    ps = cast(ParameterServer, self.server.parameter_server)
                    if handler_type == 'get_param':
                        ps.handle_get_param(msg)
                        trainer_type = msg.get('trainer_type')
                        with ps.state.param_lock:
                            if trainer_type == 'mortal':
                                params = ps.state.mortal_param
                                version = ps.state.param_version
                            else:
                                params = ps.state.dqn_param
                                version = ps.state.param_version
                        response = {
                            'type': 'param_response',
                            'params': params,
                            'version': version
                        }
                    elif handler_type == 'submit_replay':
                        ps.handle_submit_replay(msg)
                        response = {'type': 'submit_replay_ack'}
                    elif handler_type == 'submit_param':
                        ps.handle_submit_param(msg)
                        response = {'type': 'submit_param_ack'}
                    elif handler_type == 'drain':
                        ps.handle_drain()
                        response = {'type': 'drain_ack'}
                    else:
                        response = {'type': 'error', 'message': 'Unknown request type'}
                    self.request.sendall(json.dumps(response).encode('utf-8'))
                except Exception as e:
                    print(f"Error handling request: {e}")
                    self.request.sendall(json.dumps({'type': 'error', 'message': str(e)}).encode('utf-8'))

        server = socketserver.ThreadingTCPServer((host, port), ParameterRequestHandler)
        server.parameter_server = self
        server.serve_forever()

    def handle_get_param(self, msg: dict) -> None:
        pass

    def handle_submit_replay(self, msg: dict) -> None:
        with self.state.dir_lock:
            if self.state.buffer_size >= self.state.capacity:
                return
            self.state.submission_id += 1
            self.state.buffer_size += 1

    def handle_submit_param(self, msg: dict) -> None:
