import socketserver
import socket
import struct
import threading

class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self) -> bytes:
        try:
            size_bytes = self.request.recv(2)
            if len(size_bytes) < 2:
                raise EOFError("Incomplete size header")
            size = int.from_bytes(size_bytes, 'big')
            data = bytearray()
            remaining = size
            while remaining > 0:
                chunk = self.request.recv(remaining)
                if not chunk:
                    raise EOFError("Connection closed during data reception")
                data.extend(chunk)
                remaining -= len(chunk)
            return bytes(data)
        except Exception:
            raise

    def send_data(self, data: bytes) -> None:
        if len(data) > 65535:
            raise ValueError("Data exceeds maximum size for 2-byte framing")
        size = len(data)
        size_bytes = size.to_bytes(2, 'big')
        try:
            self.request.sendall(size_bytes + data)
        except Exception:
            raise

    def handle(self) -> None:
        try:
            while True:
                data = self.get_data()
                self.send_data(data)
        except (EOFError, ConnectionResetError, OSError):
            pass
        except Exception:
            pass

