            self.tcp_server = ThreadedTCPServer(("", self.port), TCPRequestHandler)
            self.servers.append(self.tcp_server)

    def start(self) -> None:
        if self.use_tcp:
            tcp_thread = threading.Thread(target=self.tcp_server.serve_forever)
            tcp_thread.daemon = True
