    def thread_loop(self):
        self.pusher = self.context.socket(zmq.PUSH)
        self.pusher.bind(self.ipc_push_url)

        self.subber = self.context.socket(zmq.SUB)
        self.subber.connect(self.ipc_sub_url)
        self.subber.setsockopt(zmq.SUBSCRIBE, b'')

        while self.running:
            try:
                rlist, _, _ = select.select(
                    [self.udp_socket, self.subber, self.thread_pipe],
                    [],
                    [],
                    1.0
                )
            except select.error:
                continue

            if not self.running:
                break

            if self.udp_socket in rlist:
                try:
                    data, addr = self.udp_socket.recvfrom(65507)
                    self.on_recv(data, addr)
                except socket.error:
                    pass

            if self.subber in rlist:
                try:
                    command = self.subber.recv(flags=zmq.NOBLOCK)
                    if self.gaze_receiver:
                        self.udp_socket.sendto(command, self.gaze_receiver)
                except zmq.Again:
                    pass

            if self.thread_pipe in rlist:
                try:
                    msg = self.thread_pipe.recv(flags=zmq.NOBLOCK)
                    if msg == b'QUIT':
                        break
                except zmq.Again:
                    pass

        self.pusher.close()
        self.subber.close()

    def on_recv(self, data, addr):
        if data.startswith(b'GAZE'):
            self.gaze_receiver = addr
        elif data.startswith(b'CALIB'):
            self.calib_result_receiver = addr
        self.pusher.send(data)
