import socket
import struct

class UDPSocketHandler:
    IP_BINDANY = 52  # Example value for IP_BINDANY (Linux-specific)
    IP_RECVDSTADDR = 15  # Example value for IP_RECVDSTADDR (Linux-specific)

    @staticmethod
    def setup_udp_listener(udp_listener):
        for attr in ['v4', 'v6']:
            sock = getattr(udp_listener, attr, None)
            if sock is None:
                continue
            if sock.family == socket.AF_INET:
                sock.setsockopt(socket.IPPROTO_IP, UDPSocketHandler.IP_RECVDSTADDR, 1)
            elif sock.family == socket.AF_INET6:
                sock.setsockopt(socket.IPPROTO_IPV6, 76, 1)  # Example for IPv6_RECVDSTADDR

    @staticmethod
    def recv_udp(listener, bufsize):
        data, ancdata, _, address = listener.recvmsg(bufsize, 20)
        srcip = address
        dstip = None
        for cmsg_level, cmsg_type, cmsg_data in ancdata:
            if cmsg_level == socket.IPPROTO_IP and cmsg_type == UDPSocketHandler.IP_RECVDSTADDR:
                dst_addr = socket.inet_ntoa(cmsg_data[:4])
                listener_port = listener.getsockname()[1]
                dstip = (dst_addr, listener_port)
                break
        return (srcip, dstip, data)

    @staticmethod
    def send_udp(sock, srcip, dstip, data):
        new_sock = socket.socket(sock.family, socket.SOCK_DGRAM)
        new_sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        new_sock.bind(srcip)
        new_sock.sendto(data, dstip)
        new_sock.close()

