            else:
                self._handle_server_dns_resolved("", (server_addr, server_port), 
                                               server_addr, (data, r_addr, None, header_length))
        except Exception as e:
            logging.error(f"Error handling server data: {e}")

    def _handle_server_dns_resolved(self, error, remote_addr, server_addr, params):
        if error:
            return
        data, r_addr, uid, header_length = params
        server_port = remote_addr[1]

        addrs = socket.getaddrinfo(server_addr, server_port, 0,
                                 socket.SOCK_DGRAM, socket.SOL_UDP)
        af, socktype, proto, canonname, sa = addrs[0]
        client = socket.socket(af, socktype, proto)
        client.setblocking(False)

        try:
            client.sendto(data[header_length:], (server_addr, server_port))
            self._sockets.add(client.fileno())
        except IOError as e:
            err = eventloop.errno_from_exception(e)
            if err not in (errno.EINPROGRESS, errno.EAGAIN):
                logging.error(f"Error sending UDP data: {e}")

    def _handle_client(self, sock):
