from websockets.legacy.client import WebSocketClientProtocol
from typing import Optional, Any, List, Tuple, Union, ClassVar, Type, TypeVar, cast
from urllib.parse import urlparse as URI

RPCResponse = TypeVar('RPCResponse')
RPCEndpoint = TypeVar('RPCEndpoint')

class PersistentWebSocket:
    def __init__(self, endpoint_uri: str, **websocket_kwargs):
        self.ws: Optional[WebSocketClientProtocol] = None
        self.endpoint_uri = endpoint_uri
        self.websocket_kwargs = websocket_kwargs

    async def __aenter__(self) -> WebSocketClientProtocol:
        if self.ws is None or self.ws.closed:
            self.ws = await websockets.connect(self.endpoint_uri, **self.websocket_kwargs)
        return self.ws

    async def __aexit__(self, exc_type: Type[BaseException], exc_val: BaseException, exc_tb: Any) -> None:
        if exc_type is not None:
            if self.ws is not None:
                await self.ws.close()


class LegacyWebSocketProvider:
    _loop: ClassVar[Any] = asyncio.get_event_loop()

    def __init__(
        self,
        endpoint_uri: Optional[Union[str, URI]] = None,
        websocket_timeout: int = 10,
        **websocket_kwargs
    ):
        self.endpoint_uri = endpoint_uri
        self.websocket_timeout = websocket_timeout
        self.conn = PersistentWebSocket(str(endpoint_uri), **websocket_kwargs)

    async def coro_make_request(self, request_data: bytes) -> RPCResponse:
        async with self.conn as ws:
            await ws.send(request_data)
            try:
                response = await asyncio.wait_for(ws.recv(), timeout=self.websocket_timeout)
                return json.loads(response)
            except asyncio.TimeoutError as e:
                raise RuntimeError("WebSocket timeout") from e
            except websockets.exceptions.WebSocketException as e:
                raise RuntimeError("WebSocket error") from e

    def make_request(self, method: RPCEndpoint, params: Any) -> RPCResponse:
        request = json.dumps({"jsonrpc": "2.0", "method": method, "params": params, "id": 1}).encode('utf-8')
        future = asyncio.run_coroutine_threadsafe(self.coro_make_request(request), self._loop)
        return future.result()

    def make_batch_request(self, requests: List[Tuple[RPCEndpoint, Any]]) -> List[RPCResponse]:
        batch = [{"jsonrpc": "2.0", "method": m, "params": p, "id": i} for i, (m, p) in enumerate(requests)]
        batch_data = json.dumps(batch).encode('utf-8')
        future = asyncio.run_coroutine_threadsafe(self.coro_make_request(batch_data), self._loop)
        return future.result()
