import websocket
import threading
import json
import queue

class Protocol:
    def __init__(self):
        self.next_id = 0
        self.commands = {}
        self.notifications = {}
        self.url = None
        self.on_open = None
        self.on_close = None
        self.socket = None
        self.lock = threading.Lock()
        self.task_queue = queue.Queue()
        self.main_thread_handler = threading.Thread(target=self._process_main_queue, daemon=True)
        self.main_thread_handler.start()

    def _process_main_queue(self):
        while True:
            task = self.task_queue.get()
            if task is None:
                break
            func, args = task
            try:
                func(*args)
            finally:
                self.task_queue.task_done()

    def to_main_thread(self, f, args):
        if threading.current_thread() == threading.main_thread():
            f(*args)
        else:
            self.task_queue.put((f, args))

    def connect(self, url, on_open=None, on_close=None):
        self.url = url
        self.on_open = on_open
        self.on_close = on_close
        self.ws_thread = threading.Thread(target=self.thread_callback, daemon=True)
        self.ws_thread.start()

    def thread_callback(self):
        self.socket = websocket.WebSocketApp(
            self.url,
            on_message=lambda ws, msg: self.message_callback(ws, msg),
            on_open=lambda ws: self.open_callback(ws),
            on_close=lambda ws: self.close_callback(ws),
        )
        self.socket.run_forever()

    def send(self, command, callback=None, options=None):
        with self.lock:
            cmd_id = self.next_id
            self.next_id += 1
            self.commands[cmd_id] = callback
        payload = {'id': cmd_id, 'command': command}
        if options:
            payload.update(options)
        self.socket.send(json.dumps(payload))

    def subscribe(self, notification, callback):
        with self.lock:
            self.notifications[notification] = callback

    def unsubscribe(self, notification):
        with self.lock:
            if notification in self.notifications:
                del self.notifications[notification]

    def message_callback(self, ws, message):
        try:
            data = json.loads(message)
        except json.JSONDecodeError:
            return
        if 'id' in data:
            cmd_id = data['id']
            with self.lock:
                callback = self.commands.pop(cmd_id, None)
            if callback:
                self.to_main_thread(callback, [data])
        elif 'notification' in data:
            notification_name = data['notification']
            with self.lock:
                callback = self.notifications.get(notification_name, None)
            if callback:
                self.to_main_thread(callback, [data])

    def open_callback(self, ws):
        if self.on_open:
            self.to_main_thread(self.on_open, [ws])

    def close_callback(self, ws):
        if self.on_close:
            self.to_main_thread(self.on_close, [ws])

