async def server(websocket, path):
    async for message in websocket:
        try:
            data = json.loads(message)
        except json.JSONDecodeError:
            await websocket.send(json.dumps({"error": "invalid JSON"}))
            continue

        function = data.get("function")
        args = data.get("args", [])

        if not isinstance(args, list):
            await websocket.send(json.dumps({"error": "args must be an array"}))
            continue

        if function == "echo":
            if len(args) >= 1:
                await websocket.send(json.dumps({"result": args[0]}))
            else:
                await websocket.send(json.dumps({"error": "missing arguments for echo"}))
        elif function == "ask":
            if len(args) >= 1:
                prompt = args[0]
                try:
                    user_input = await asyncio.to_thread(input, prompt)
                    await websocket.send(json.dumps({"result": user_input}))
                except Exception as e:
                    await websocket.send(json.dumps({"error": str(e)}))
            else:
                await websocket.send(json.dumps({"error": "missing arguments for ask"}))
        elif function == "throw":
            if len(args) >= 1:
                await websocket.send(json.dumps({"error": args[0]}))
            else:
                await websocket.send(json.dumps({"error": "missing arguments for throw"}))
        else:
            await websocket.send(json.dumps({"error": f"unknown function: {function}"}))

