from yaspin import Yaspin

class Device:
    def __init__(self):
        self.server_url = "ws://localhost:8765"
        self.p = pyaudio.PyAudio()
        self.websocket = None
        self.recording = False
        self.input_stream = None
        self.spinner = Yaspin()
        self.play_audio = True
        self.CHUNK = 1024
        self.FORMAT = pyaudio.paInt16
        self.CHANNELS = 1
        self.RECORDING_RATE = 16000
        self.PLAYBACK_RATE = 24000
        self.output_stream = self.p.open(
            format=self.FORMAT,
            channels=self.CHANNELS,
            rate=self.PLAYBACK_RATE,
            output=True,
            frames_per_buffer=self.CHUNK
        )
        keyboard.on_press(self.on_press)
        keyboard.on_release(self.on_release)

    async def connect_with_retry(self, max_retries=50, retry_delay=2):
        for _ in range(max_retries):
            try:
                self.websocket = await websockets.connect(self.server_url)
                print("Connected to server.")
                return
            except (websockets.exceptions.ConnectionClosedError, websockets.exceptions.InvalidHandshake, OSError) as e:
                print(f"Connection failed: {e}. Retrying in {retry_delay} seconds...")
                await asyncio.sleep(retry_delay)
        print("Failed to connect after multiple attempts.")

    async def send_audio(self):
        while True:
            if self.recording:
                if self.input_stream is None:
                    self.input_stream = self.p.open(
                        format=self.FORMAT,
                        channels=self.CHANNELS,
                        rate=self.RECORDING_RATE,
                        input=True,
                        frames_per_buffer=self.CHUNK
                    )
                try:
                    data = self.input_stream.read(self.CHUNK)
                    await self.websocket.send(data)
                except Exception as e:
                    print(f"Error sending audio: {e}")
                    self.recording = False
                    if self.input_stream:
                        self.input_stream.stop_stream()
                        self.input_stream.close()
                        self.input_stream = None
            else:
                if self.input_stream:
                    self.input_stream.stop_stream()
                    self.input_stream.close()
                    self.input_stream = None
            await asyncio.sleep(0.01)

    async def receive_audio(self):
        try:
            async for message in self.websocket:
                if self.play_audio:
                    self.output_stream.write(message)
        except websockets.exceptions.ConnectionClosed:
            pass

    def on_press(self, key):
        try:
            if key == keyboard.Key.ctrl:
                self.recording = True
                self.spinner.start()
        except Exception as e:
            print(f"Error in on_press: {e}")

    def on_release(self, key):
        try:
            if key == keyboard.Key.ctrl:
                self.recording = False
                self.spinner.stop()
        except Exception as e:
            print(f"Error in on_release: {e}")

    async def main(self):
        await self.connect_with_retry()
        send_task = asyncio.create_task(self.send_audio())
        receive_task = asyncio.create_task(self.receive_audio())
        try:
            await asyncio.gather(send_task, receive_task)
        except Exception as e:
            print(f"Main loop error: {e}")
        finally:
            if self.input_stream:
                self.input_stream.stop_stream()
                self.input_stream.close()
            if self.output_stream:
                self.output_stream.stop_stream()
                self.output_stream.close()
            self.p.terminate()

    def start(self):
        try:
            asyncio.run(self.main())
        except KeyboardInterrupt:
            print("Shutting down...")
            if self.input_stream:
                self.input_stream.stop_stream()
                self.input_stream.close()
            if self.output_stream:
                self.output_stream.stop_stream()
                self.output_stream.close()
            self.p.terminate()
