import zmq
import threading
import time
from abc import ABC, abstractmethod

class RpcClient(ABC):
    HEARTBEAT_TIMEOUT = 5.0  # seconds

    def __init__(self):
        self._context = zmq.Context()
        self._socket_sub = self._context.socket(zmq.SUB)
        self._socket_sub.setsockopt(zmq.LINGER, -1)
        self._active = False
        self._thread = None
        self._lock = threading.Lock()
        self._last_received_ping = 0.0

    def start(self, sub_address: str) -> None:
        self._socket_sub.connect(sub_address)
        self._active = True
        self._thread = threading.Thread(target=self.run)
        self._thread.start()

    def stop(self) -> None:
        with self._lock:
            self._active = False

    def join(self) -> None:
        if self._thread:
            self._thread.join()

    def run(self) -> None:
        while self._active:
            poller = zmq.Poller()
            poller.register(self._socket_sub, zmq.POLLIN)
            if poller.poll(1000):  # 1 second timeout
                try:
                    message_parts = self._socket_sub.recv_multipart()
                    topic = message_parts[0]
                    data = message_parts[1] if len(message_parts) > 1 else b''
                    self._last_received_ping = time.time()
                    self.callback(topic, data)
                except zmq.ZMQError:
                    pass
            else:
                if time.time() - self._last_received_ping > self.HEARTBEAT_TIMEOUT:
                    self.on_disconnected()

    @abstractmethod
    def callback(self, topic: bytes, data: bytes) -> None:
        pass

    def subscribe_topic(self, topic: str) -> None:
        self._socket_sub.setsockopt(zmq.SUBSCRIBE, topic.encode('utf-8'))

    def on_disconnected(self) -> None:
        print("Warning: Disconnected from publisher due to heartbeat timeout.")
