import zmq
import numpy as np
import json
import base64
import uuid
import time
from typing import Optional, List, Generator, Union

class Response:
    def __init__(self, request_id: int, array: np.ndarray):
        self.id = request_id
        self.array = array

    def __repr__(self):
        return f"Response(id={self.id}, array={self.array})"

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.sender.connect(f"tcp://{ip}:{port}")
        self.receiver = self.context.socket(zmq.SUB)
        self.receiver.connect(f"tcp://{ip}:{port_out}")
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')
        self.identity = uuid.uuid4().hex.encode('ascii') if identity is None else identity.encode('ascii')
        self.request_id = 0
        self.timeout = timeout
        if self.timeout >= 0:
            self.sender.setsockopt(zmq.SNDTIMEO, self.timeout * 1000)
            self.receiver.setsockopt(zmq.RCVTIMEO, self.timeout * 1000)
        self.pending_request = set()

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, texts, msg_len=0):
        self.request_id += 1
        payload = {
            'identity': self.identity.decode('ascii'),
            'id': self.request_id,
            'texts': texts
        }
        msg = json.dumps(payload).encode('utf-8')
        self.sender.send(msg)
        self.pending_request.add(self.request_id)

    def _recv(self):
        try:
            msg = self.receiver.recv()
        except zmq.Again:
            return None
        try:
            data = json.loads(msg.decode('utf-8'))
        except json.JSONDecodeError:
            return None
        if data.get('identity') != self.identity.decode('ascii'):
            return None
        req_id = data.get('id')
        if req_id in self.pending_request:
            self.pending_request.remove(req_id)
            return Response(req_id, data.get('result', {}))
        return None

    def _recv_ndarray(self) -> Optional[Response]:
        res = self._recv()
        if res is None:
            return None
        result_data = res.array
        try:
            dtype = np.dtype(result_data['dtype'])
            shape = result_data['shape']
            buffer = base64.b64decode(result_data['data'])
            arr = np.frombuffer(buffer, dtype=dtype).reshape(shape)
        except (KeyError, ValueError, TypeError):
            return None
        return Response(res.id, arr)

    def encode(self, texts, blocking=True) -> Optional[np.ndarray]:
        self._send(texts)
        current_id = self.request_id
        if blocking:
            while True:
                res = self._recv_ndarray()
                if res and res.id == current_id:
                    return res.array
                time.sleep(0.001)
        else:
            return None

    def fetch(self, delay=0.0) -> Generator[Response, None, None]:
        while True:
            res = self._recv_ndarray()
            if res is not None:
                yield res
            else:
                time.sleep(delay)

    def fetch_all(self, sort=True, concat=False) -> Union[List[np.ndarray], np.ndarray]:
        results = []
        while True:
            res = self._recv_ndarray()
            if res is None:
                break
            results.append(res)
        if sort:
            results.sort(key=lambda x: x.id)
        arrays = [r.array for r in results]
        if concat and arrays:
            return np.concatenate(arrays)
        else:
            return arrays
