import zmq
import threading
from collections import deque
import zlib
import numpy as np

class NetGear:
    def __init__(self, address=None, port=None, protocol='tcp', pattern=0, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__subscriber_timeout = options.get('subscriber_timeout', 1)
        self.__queue = deque()
        self.__thread = None
        self.compression_enabled = options.get('compression', False)

        if address and port:
            self.full_address = f"{protocol}://{address}:{port}"
        else:
            self.full_address = address or ""

        if self.__pattern == 2:
            if receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.SUB)
                self.__msg_socket.connect(self.full_address)
                self.__msg_socket.setsockopt(zmq.SUBSCRIBE, b'')
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.PUB)
                self.__msg_socket.bind(self.full_address)
            self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout * 1000)
        elif self.__pattern == 0:
            if receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.PAIR)
                self.__msg_socket.connect(self.full_address)
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.PAIR)
                self.__msg_socket.bind(self.full_address)
        elif self.__pattern == 1:
            if receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.REP)
                self.__msg_socket.connect(self.full_address)
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.REQ)
                self.__msg_socket.bind(self.full_address)

        if receive_mode:
            self.__thread = threading.Thread(target=self.__recv_handler)
            self.__thread.daemon = True
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                parts = self.__msg_socket.recv_multipart()
                if len(parts) >= 2:
                    message_part = parts[0]
                    frame_data = parts[1]
                    if self.compression_enabled:
                        frame = zlib.decompress(frame_data)
                    else:
                        frame = frame_data
                    self.__queue.append(frame)
            except zmq.Again:
                continue
            except Exception as e:
                if self.__logging:
                    print(f"Receive error: {e}")
                continue

    def recv(self):
        if self.__queue:
            return self.__queue.popleft()
        return None

    def send(self, frame, message=None):
        msg_part = message.encode() if isinstance(message, str) else message or b''
        if self.compression_enabled:
            frame_data = zlib.compress(frame)
        else:
            frame_data = frame
        self.__msg_socket.send_multipart([msg_part, frame_data])

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread and self.__thread.is_alive():
            self.__thread.join()
        self.__msg_socket.close()
        self.__msg_context.term()
