import httpx
import hmac
import hashlib
import json
from typing import Dict, Any

class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(self, payload: Dict[str, Any], signature_secret: str, additional_payload_values: Dict[str, Any] = None, additional_headers: Dict[str, str] = None, transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)) -> 'ExternalActionResponse':
        additional_payload_values = additional_payload_values or {}
        additional_headers = additional_headers or {}
        merged_payload = {**payload, **additional_payload_values}
        payload_bytes = json.dumps(merged_payload).encode('utf-8')
        signature = self._encode_payload(payload_bytes, signature_secret)
        headers = {
            'Content-Type': 'application/json',
            'X-Signature': signature,
            **additional_headers
        }
        async with httpx.AsyncClient(transport=transport) as client:
            try:
                response = await client.post(self.url, content=payload_bytes, headers=headers)
                response.raise_for_status()
                response_data = response.json()
                return self._validate_response(response_data)
            except httpx.HTTPStatusError as e:
                return ExternalActionResponse(success=False, error=str(e))
            except httpx.RequestError as e:
                return ExternalActionResponse(success=False, error=str(e))
            except json.JSONDecodeError as e:
                return ExternalActionResponse(success=False, error="Invalid JSON response")

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        hmac_obj = hmac.new(signature_secret.encode('utf-8'), payload, hashlib.sha256)
        return hmac_obj.hexdigest()

    def _validate_response(self, response: Dict[str, Any]) -> 'ExternalActionResponse':
        if response.get('status') == 'success':
            return ExternalActionResponse(success=True, data=response.get('data'))
        else:
            return ExternalActionResponse(success=False, error=response.get('error', 'Unknown error'))
