import http.client
import queue
from typing import Optional, Union, Mapping, Any, Dict

class HTTPConnectionPool:
    def __init__(self, host, port=None, timeout=None, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.host = host
        self.port = port
        self.timeout = timeout
        self.maxsize = maxsize
        self.block = block
        self.headers = headers or {}
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.num_connections = 0

        if self.port is None:
            self.port = 80
            self.scheme = 'http'
        else:
            if self.port == 443:
                self.scheme = 'https'
            else:
                self.scheme = 'http'

        if self.scheme == 'https':
            self.ConnectionCls = http.client.HTTPSConnection
        else:
            self.ConnectionCls = http.client.HTTPConnection

        self.pool = queue.LifoQueue(maxsize=self.maxsize)

    def _new_conn(self):
        if self.proxy:
            proxy_host = self.proxy.host
            proxy_port = self.proxy.port
            conn = self.ConnectionCls(proxy_host, proxy_port, **self.conn_kw)
            conn.set_tunnel(self.host, self.port, headers=self.proxy_headers)
        else:
            conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        self.num_connections += 1
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            if self.num_connections < self.maxsize:
                conn = self._new_conn()
            else:
                if self.block:
                    conn = self.pool.get(timeout=timeout)
                else:
                    raise queue.Empty()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except queue.Full:
            if not self.block:
                conn.close()

    def _make_request(self, conn, method, url, body, headers, retries, timeout, **response_kw):
        final_headers = self.headers.copy()
        if headers:
            final_headers.update(headers)
        if isinstance(timeout, (float, int)):
            conn.timeout = timeout
        elif timeout is not None:
            conn.timeout = timeout.connect_timeout
        conn.request(method, url, body=body, headers=final_headers)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=None, pool_timeout=None, **response_kw):
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, body, headers, retries, timeout, **response_kw)
            if redirect and response.status in [301, 302, 303, 307, 308]:
                location = response.getheader('Location')
                if assert_same_host:
                    pass
                return self.urlopen(method, location, body, headers, retries, redirect, timeout, pool_timeout, **response_kw)
            return response
        finally:
            self._put_conn(conn)

    def _close_pool_connections(self, pool):
        while not pool.empty():
            try:
                conn = pool.get_nowait()
                conn.close()
            except queue.Empty:
                break

    def close(self):
        self._close_pool_connections(self.pool)

