import requests

class AccuWeatherClient:
    def __init__(self, api_key: str = "") -> None:
        self.api_key = api_key

    def get_weather(self, city: str, extensions: str = "all") -> str:
        if not self.api_key:
            return "Error: No API key provided."

        location_key = self._get_location_key(city)
        if not location_key:
            return "Error: Could not find location key for the city."

        if extensions == "all":
            url = f"http://dataservice.accuweather.com/currentconditions/v1/{location_key}?apikey={self.api_key}&details=true"
            response = requests.get(url)
            if response.status_code != 200:
                return "Error: Failed to fetch current weather data."
            data = response.json()
            return self._format_current_conditions(data)
        elif extensions == "base":
            url = f"http://dataservice.accuweather.com/forecasts/v1/daily/5day/{location_key}?apikey={self.api_key}"
            response = requests.get(url)
            if response.status_code != 200:
                return "Error: Failed to fetch forecast data."
            data = response.json()
            return self._format_forecast(data)
        else:
            return "Error: Invalid extensions parameter."

    def _get_location_key(self, city: str) -> str:
        url = f"http://dataservice.accuweather.com/locations/v1/cities/search?apikey={self.api_key}&q={city}"
        response = requests.get(url)
        if response.status_code == 200:
            results = response.json()
            if results:
                return results[0]['Key']
        return ""

    def _format_current_conditions(self, data) -> str:
        if not data:
            return "No current conditions data."
        current = data[0]
        temp = current['Temperature']['Metric']['Value']
        weather_text = current['WeatherText']
        return f"Current weather: {weather_text}. Temperature: {temp}°C"

    def _format_forecast(self, data) -> str:
        if not data or 'DailyForecasts' not in data:
            return "No forecast data available."
        forecasts = data['DailyForecasts']
        result = "5-Day Forecast:\n"
        for day in forecasts:
            date = day['Date'].split('T')[0]
            high = day['Temperature']['Maximum']['Value']
            low = day['Temperature']['Minimum']['Value']
            result += f"{date}: High {high}°C, Low {low}°C\n"
        return result.strip()

