        data = {
            "client_id": self.client_id,
            "client_secret": self.client_secret,
            "refresh_token": self.refresh_token,
            "grant_type": "refresh_token"
        }
        headers = {"Accept": "application/json"}
        response = requests.post(url, data=data, headers=headers)
        try:
            response.raise_for_status()
        except requests.HTTPError as e:
            raise HTTPException(status_code=e.response.status_code, detail="Failed to refresh token") from e
        tokens = response.json()
        self.access_token = tokens["access_token"]
        self.refresh_token = tokens["refresh_token"]
        return self.access_token

    def get_user_info(self):
        headers = {"Authorization": f"token {self.access_token}"}
        user_url = "https://api.github.com/user"
        user_response = requests.get(user_url, headers=headers)
        try:
            user_response.raise_for_status()
        except requests.HTTPError as e:
            raise HTTPException(status_code=e.response.status_code, detail="Failed to get user info") from e
        user_data = user_response.json()
        name_parts = user_data.get("name", "").split(" ", 1)
        first_name = name_parts[0] if len(name_parts) > 0 else ""
        last_name = name_parts[1] if len(name_parts) > 1 else ""

        emails_url = "https://api.github.com/user/emails"
