import requests
import redis
from typing import Callable, Optional, Dict, Any

class RunKeeperAPI:
    _RATE_LIMIT_KEY = "runkeeper_rate_limit:{endpoint}"

    def __init__(self, redis_host='localhost', redis_port=6379, redis_db=0):
        self.redis = redis.Redis(host=redis_host, port=redis_port, db=redis_db)

    def _rate_limit(self, endpoint: str, req_lambda: Callable) -> requests.Response:
        redis_key = self._RATE_LIMIT_KEY.format(endpoint=endpoint)
        if self.redis.exists(redis_key):
            from requests.models import Response
            resp = Response()
            resp.status_code = 429
            return resp
        try:
            response = req_lambda()
            if response.status_code == 429:
                retry_after = int(response.headers.get('Retry-After', 60))
                self.redis.setex(redis_key, retry_after, '1')
            return response
        except Exception as e:
            raise

    def _api_headers(self, service_record: Any) -> Dict[str, str]:
        return {
            'Authorization': f'Bearer {service_record.access_token}',
            'Content-Type': 'application/json',
            'User-Agent': 'RunKeeperAPI/1.0'
        }

    def get_request(self, url: str, service_record: Any) -> dict:
        headers = self._api_headers(service_record)
        response = self._rate_limit(url, lambda: requests.get(url, headers=headers))
        if response.status_code == 200:
            return response.json()
        else:
            response.raise_for_status()

    def post_request(self, url: str, service_record: Any, data: Optional[dict] = None) -> requests.Response:
        headers = self._api_headers(service_record)
        response = self._rate_limit(url, lambda: requests.post(url, headers=headers, json=data))
        return response

    def delete_request(self, url: str, service_record: Any) -> requests.Response:
        headers = self._api_headers(service_record)
        response = self._rate_limit(url, lambda: requests.delete(url, headers=headers))
        return response

    def oauth_token_request(self, params: Dict[str, Any]) -> str:
        token_url = 'https://api.runkeeper.com/oauth2/token'
        client_id = params.pop('client_id', None)
        client_secret = params.pop('client_secret', None)
        if not client_id or not client_secret:
            raise ValueError("client_id and client_secret are required")
        client_auth = requests.auth.HTTPBasicAuth(client_id, client_secret)
        response = requests.post(token_url, auth=client_auth, data=params)
        response.raise_for_status()
        return response.json()['access_token']

