from urllib.parse import urlparse

class BlockchainClient:
    def __init__(self):
        self.nodes = set()

    def register_node(self, address: str) -> None:
        parsed_url = urlparse(address)
        base_url = f"{parsed_url.scheme}://{parsed_url.netloc}"
        self.nodes.add(base_url)

    def resolve_conflicts(self) -> Tuple[bool, Optional[List]]:
        max_length = 0
        longest_chain = None
        for node in self.nodes:
            success, chain_data = self.get_chain(node)
            if success:
                chain_length = chain_data.get('length', 0)
                current_chain = chain_data.get('chain')
                if chain_length > max_length:
                    max_length = chain_length
                    longest_chain = current_chain
        return (True, longest_chain) if longest_chain else (False, None)

    def get_chain(self, node: str) -> Tuple[bool, Optional[Dict]]:
