import base64
import requests

class AmazonSSO:
    def __init__(self, access_token: str = None, refresh_token: str = None):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = None
        self.client_secret = None
        self.user_pool_id = None
        self.region = None
        self.user_info = {}

    def get_new_token(self) -> str:
        token_url = f"https://{self.user_pool_id.split('_')[1]}.auth.{self.region}.amazoncognito.com/oauth2/token"
        auth = base64.b64encode(f"{self.client_id}:{self.client_secret}".encode()).decode()
        headers = {
            'Authorization': f'Basic {auth}',
            'Content-Type': 'application/x-www-form-urlencoded'
        }
        data = {
            'grant_type': 'refresh_token',
            'refresh_token': self.refresh_token
        }
        response = requests.post(token_url, headers=headers, data=data)
        response.raise_for_status()
        tokens = response.json()
        self.access_token = tokens['access_token']
        if 'refresh_token' in tokens:
            self.refresh_token = tokens['refresh_token']
        return self.access_token

    def get_user_info(self) -> dict:
        if not self.user_info:
            domain_part = self.user_pool_id.split('_')[1]
            user_info_url = f"https://{domain_part}.auth.{self.region}.amazoncognito.com/oauth2/userInfo"
            headers = {
                'Authorization': f'Bearer {self.access_token}'
            }
            response = requests.get(user_info_url, headers=headers)
            response.raise_for_status()
            self.user_info = response.json()
        return self.user_info

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str, client_id: str, client_secret: str, user_pool_id: str, region: str):
        domain_part = user_pool_id.split('_')[1]
        token_url = f"https://{domain_part}.auth.{region}.amazoncognito.com/oauth2/token"
        auth = base64.b64encode(f"{client_id}:{client_secret}".encode()).decode()
        headers = {
            'Authorization': f'Basic {auth}',
            'Content-Type': 'application/x-www-form-urlencoded'
        }
        data = {
            'grant_type': 'authorization_code',
            'code': code,
            'redirect_uri': redirect_uri
        }
        response = requests.post(token_url, headers=headers, data=data)
        if response.status_code != 200:
            return None, None
        tokens = response.json()
        instance = AmazonSSO()
        instance.client_id = client_id
        instance.client_secret = client_secret
        instance.user_pool_id = user_pool_id
        instance.region = region
        instance.access_token = tokens['access_token']
        instance.refresh_token = tokens['refresh_token']
        return instance, None

