import threading
import time
import requests
from typing import Dict, Optional
import logging

class Fetcher:
    def __init__(self, base_url: str, api_key: str) -> None:
        self._base_url = base_url
        self._api_key = api_key
        self._stop_polling_event = threading.Event()
        self._exit_monitor = threading.Thread(target=self._monitor_exit, args=(self._stop_polling_event,))
        self._poller_thread = threading.Thread(target=self._thread_func, args=(self._base_url, self._api_key, self._stop_polling_event, 30))
        self._exit_monitor.daemon = True
        self._poller_thread.daemon = True

    def run(self) -> None:
        self._exit_monitor.start()
        self._poller_thread.start()

    def post(self, api: str, body: Dict[str, str]) -> None:
        url = f"{self._base_url}/{api}"
        self._post_url(url, self._api_key, body)

    def _fetch_url(self, url: str, api_key: str) -> Dict:
        headers = {"Authorization": f"Bearer {api_key}"}
        retries = 5
        for i in range(retries):
            try:
                response = requests.get(url, headers=headers, timeout=10)
                if response.status_code == 200:
                    return response.json()
                else:
                    raise Exception(f"HTTP error: {response.status_code}")
            except Exception as e:
                wait_time = (2 ** i) + (i * 0.1)
                logging.warning(f"Request failed: {e}. Retrying in {wait_time} seconds...")
                time.sleep(wait_time)
        raise Exception("All retries failed")

    def _post_url(self, url: str, api_key: str, body: Dict[str, str]) -> None:
        headers = {"Authorization": f"Bearer {api_key}", "Content-Type": "application/json"}
        try:
            response = requests.post(url, json=body, headers=headers, timeout=10)
            if response.status_code != 200:
                raise Exception(f"HTTP error: {response.status_code}")
        except Exception as e:
            logging.error(f"POST request failed: {e}")

    def _thread_func(self, base_url: str, api_key: str, stop_polling_event: threading.Event, seconds_interval: Optional[int] = 60) -> None:
        while not stop_polling_event.wait(seconds_interval):
            self._refresh_data(base_url, api_key)

    def _refresh_data(self, base_url: str, api_key: str) -> None:
        endpoints = ["data1", "data2"]
        for endpoint in endpoints:
            url = f"{base_url}/{endpoint}"
            result = self._fetch_url(url, api_key)
            logging.info(f"Refreshed data from {url}: {result}")

    def _monitor_exit(self, exit_event: threading.Event) -> None:
        import atexit
        atexit.register(exit_event.set)
