class ServeCommand:
    def __init__(self, pipeline, host: str = "localhost", port: int = 8888, workers: int = 1) -> None:
        self._pipeline = pipeline
        self.host = host
        self.port = port
        self.workers = workers
        self._app = FastAPI()

        @self._app.get("/model_info")
        def model_info():
            return self.model_info().dict()

        @self._app.post("/tokenize")
        def tokenize(text_input: str = Body(None, embed=True), return_ids: bool = Body(False, embed=True)):
            result = self.tokenize(text_input, return_ids)
            return result.dict()

        @self._app.post("/detokenize")
        def detokenize(tokens_ids: List[int] = Body(None, embed=True), skip_special_tokens: bool = Body(False, embed=True), cleanup_tokenization_spaces: bool = Body(True, embed=True)):
            result = self.detokenize(tokens_ids, skip_special_tokens, cleanup_tokenization_spaces)
            return result.dict()

        @self._app.post("/forward")
        def forward(inputs=Body(None, embed=True)):
            result = self.forward(inputs)
            return result.dict()

    def run(self) -> None:
        import uvicorn
        uvicorn.run(self._app, host=self.host, port=self.port, workers=self.workers)

    def model_info(self) -> ServeModelInfoResult:
        return ServeModelInfoResult(
            model_name=self._pipeline.model.name_or_path,
            tokenizer_type=str(type(self._pipeline.tokenizer)),
            model_type=str(type(self._pipeline.model)),
            vocab_size=self._pipeline.tokenizer.vocab_size,
            hidden_size=self._pipeline.model.config.hidden_size,
            num_layers=self._pipeline.model.config.num_hidden_layers,
            num_attention_heads=self._pipeline.model.config.num_attention_heads
        )

    def tokenize(self, text_input: str = None, return_ids: bool = False) -> ServeTokenizeResult:
        tokens = self._pipeline.tokenizer.tokenize(text_input)
        if return_ids:
            token_ids = self._pipeline.tokenizer.convert_tokens_to_ids(tokens)
            return ServeTokenizeResult(tokens=tokens, token_ids=token_ids)
        return ServeTokenizeResult(tokens=tokens, token_ids=[])

    def detokenize(self, tokens_ids: List[int], skip_special_tokens: bool = False, cleanup_tokenization_spaces: bool = True) -> ServeDeTokenizeResult:
        text = self._pipeline.tokenizer.decode(tokens_ids, skip_special_tokens=skip_special_tokens, clean_up_tokenization_spaces=cleanup_tokenization_spaces)
        return ServeDeTokenizeResult(text=text)

    def forward(self, inputs: dict) -> ServeForwardResult:
        outputs = self._pipeline.model(**inputs)
        generated_text = self._pipeline.tokenizer.decode(outputs.logits.argmax(-1)[0])
        return ServeForwardResult(logits=outputs.logits.tolist(), generated_text=generated_text)
