        if content_type == "text/csv":
            reader = csv.DictReader(lines)
            texts = [row["text"] for row in reader]
        else:
            texts = lines

        results = []
        for text in texts:
            predictions = scoring_service(text)
            results.append({"text": text, "predictions": predictions})

        if content_type == "text/csv":
            output = StringIO()
            writer = csv.writer(output)
            writer.writerow(["text", "predictions"])
            for result in results:
                writer.writerow([result["text"], ",".join(result["predictions"])])
            return Response(output.getvalue(), status=200, mimetype="text/csv")
        else:
            output = "\n".join([f"{result['text']}|{','.join(result['predictions'])}" for result in results])
            return Response(output, status=200, mimetype="text/plain")

    else:
        return Response("Unsupported Content-Type", status=415, mimetype="text/plain")
