from faker import Faker


class SubscriptionServer:
    def __init__(self, app_name: str = "web", logger: logging.Logger = None) -> None:
        self.app = Flask(app_name)
        self.logger = logger or logging.getLogger(__name__)
        self.fake = Faker()
        self._setup_routes()

    def _setup_routes(self) -> None:
        @self.app.route("/subscribe/<sub_type>", methods=["GET"])
        def handle_subscription(sub_type):
            return self._handle_subscription(sub_type)

    def _handle_subscription(self, sub_type: str) -> Any:
        user_agent = request.headers.get("User-Agent")
        config = self._generate_config(sub_type, user_agent)
        if not config:
            return "Unsupported subscription type", 400
        return Response(config, mimetype="text/plain")

    def _generate_config(self, sub_type: str, user_agent: str) -> str:
        if "Clash" in user_agent:
            return f"[Proxy]\ntype: http\nurl: example.com\n{self._get_random_proxy_line()}"
        elif "WireGuard" in user_agent:
            return "[Interface]\nPrivateKey = fake_private_key\nAddress = 192.168.1.1/24\n"
        elif "Surge" in user_agent:
            return "[General]\nmode = Global\n[Rule]\nDOMAIN-SUFFIX,example.com,DIRECT\n"
        return ""

    def _get_random_proxy_line(self) -> str:
        return f"# {self.fake.name()} - {self.fake.ipv4()}"

    def run(self, host: str = '0.0.0.0', port: int = 5000, debug: bool = True) -> None:
        self.app.run(host=host, port=port, debug=debug)
