import os
import json
import cv2
import numpy as np

app = Flask(__name__)

GALLERY_PATH = "gallery_videos"
POSTERS_PATH = "posters"
UPLOADS_PATH = "uploads"

@app.route('/healthy', methods=['GET'])
def healthy():
    return "OK", 200

@app.route('/gallery_video/<path:path>', methods=['GET'])
def send_gallery_video(path):
    if not os.path.exists(os.path.join(GALLERY_PATH, path)):
        raise ValueError("File not found")
    return send_from_directory(GALLERY_PATH, path)

@app.route('/poster_image/<path:path>', methods=['GET'])
def send_poster_image(path):
    if not os.path.exists(os.path.join(POSTERS_PATH, path)):
        raise ValueError("File not found")
    return send_from_directory(POSTERS_PATH, path)

@app.route('/uploaded_video/<path:path>', methods=['GET'])
def send_uploaded_video(path):
    if not os.path.exists(os.path.join(UPLOADS_PATH, path)):
        raise ValueError("File not found")
    return send_from_directory(UPLOADS_PATH, path)

@app.route('/propagate_in_video', methods=['POST'])
def propagate_in_video():
    data = request.get_json()
    session_id = data.get('session_id')
    start_frame_index = data.get('start_frame_index', 0)

    video_path = f"{UPLOADS_PATH}/{session_id}.mp4"
    cap = cv2.VideoCapture(video_path)

    def generate():
        frame_idx = 0
        while True:
            ret, frame = cap.read()
            if not ret or frame_idx < start_frame_index:
                frame_idx += 1
                continue
            height, width = frame.shape[:2]
            mask = np.zeros((height, width), dtype=np.uint8)
            # Simulate a tracking mask for demonstration
            cv2.rectangle(mask, (width // 4, height // 4), (3 * width // 4, 3 * height // 4), 255, -1)
            _, buffer_mask = cv2.imencode('.jpg', mask)
            yield (b'--frame\r\n'
                   b'Content-Type: image/jpeg\r\n\r\n' + buffer_mask.tobytes() + b'\r\n')
            frame_idx += 1
            if cv2.waitKey(1) & 0xFF == ord('q'):
                break
        cap.release()

    return Response(generate(), mimetype='multipart/x-mixed-replace; boundary=frame')

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=5000, debug=True)
