from werkzeug.serving import BaseWSGIServer
from mii import MIIEngineClient

class RestfulGatewayThread(threading.Thread):
    def __init__(self, deployment_name: str, rest_host: str, rest_port: int, rest_procs: int) -> None:
        super().__init__()
        self._deployment_name = deployment_name
        self._rest_host = rest_host
        self._rest_port = rest_port
        self._rest_procs = rest_procs
        self._stop_event = threading.Event()
        self.server = None

    def _create_server(self, deployment_name: str, rest_host: str, rest_port: int, rest_procs: int) -> BaseWSGIServer:
        app = Flask(__name__)
        client = MIIEngineClient(deployment_name)

        @app.route('/v1/models/<model_id>/infer', methods=['POST'])
        def infer(model_id):
            data = request.json
            result = client.infer(data)
            return jsonify(result)

        @app.route('/health', methods=['GET'])
        def health():
            return jsonify({"status": "healthy"})

        @app.route('/terminate', methods=['POST'])
        def terminate():
            self._stop_event.set()
            return jsonify({"status": "shutdown initiated"})

        server = BaseWSGIServer(rest_host, rest_port, app, threaded=True)
        return server

    def _shutdown(self) -> None:
        if self.server is not None:
            self.server.shutdown()

    def run(self) -> None:
        self.server = self._create_server(
            self._deployment_name,
            self._rest_host,
            self._rest_port,
            self._rest_procs
        )
        self.server.serve_forever()

    def get_stop_event(self) -> threading.Event:
        return self._stop_event
