        self.app = Flask(__name__)
        self.model = model
        self.cors = CORS(self.app)
        self._setup_routes()

    def _setup_routes(self):
        self.app.route('/stream', methods=['GET'])(self.stream)
        self.app.route('/methods', methods=['GET'])(self.methods)

    def run(self, host="0.0.0.0", port=5000, debug=True):
