            for output in self.model.process_stream():
                yield f"data: {output}\n\n"
        return Response(generate(), mimetype='text/event-stream')

    def methods(self):
        available_methods = {
            "available": [
                {"endpoint": "/stream", "method": "GET", "description": "Stream model responses"},
                {"endpoint": "/methods", "method": "GET", "description": "List available endpoints"}
            ]
        }
