        spec = {
            "openapi": "3.0.0",
            "info": {"title": "User Management API", "version": "1.0"},
            "paths": {
                "/users": {
                    "get": {"summary": "List users"},
                    "post": {"summary": "Create user"}
                },
                "/users/{user_id}": {
                    "get": {"summary": "Get user by ID"},
                    "put": {"summary": "Update user by ID"},
                    "delete": {"summary": "Delete user by ID"}
                },
                "/orders/{order_id}": {
                    "delete": {"summary": "Delete order by ID"}
                }
            }
        }
        return jsonify(spec)

    def get_user(self, user_id):
        if user_id not in self.users:
            return jsonify({"error": "User not found"}), 404
        return jsonify(self.users[user_id])

    def list_users(self):
        return jsonify(list(self.users.values()))

    def create_user(self):
        data = request.get_json()
        if self.config.return_500_on_create:
            return jsonify({"error": "Internal server error"}), 500
        user_id = self.next_user_id
        self.next_user_id += 1
        user_data = {**data, "id": user_id}
        self.users[user_id] = user_data
        return jsonify(user_data), 201

    def update_user(self, user_id):
        if user_id not in self.users:
            return jsonify({"error": "User not found"}), 404
        data = request.get_json()
        self.users[user_id].update(data)
        return jsonify(self.users[user_id])

    def delete_user(self, user_id):
        if user_id not in self.users:
            return jsonify({"error": "User not found"}), 404
        del self.users[user_id]
        return '', 204

    def delete_order(self, order_id):
        self.deleted_orders.add(order_id)
        return '', 204

    def check_auth(self):
        auth_header = request.headers.get('Authorization')
        if not auth_header or auth_header != 'Bearer secret_token':
            return jsonify({"error": "Unauthorized"}), 401

if __name__ == '__main__':
