            return self._serve_webui("index.html")
        return send_from_directory(self.config.webui_dir, file_name)

    def _run(self) -> None:
        self.wsgiserver = WSGIServer((self.config.host, self.config.port), self.flask_app)
        try:
            self.wsgiserver.serve_forever()
        except Exception as e:
            logging.error(f"Server error: {e}")
        finally:
            self.stop_event.set()

    def start(self) -> None:
        self.flask_app.run_task = self._run
        self.flask_app.run_task()
        self.stop_event.wait()

    def stop(self) -> None:
        if self.wsgiserver:
            self.wsgiserver.stop()
        self.stop_event.set()

    def unhandled_exception(self, exception: Exception) -> Response:
        logging.critical(f"Unhandled exception: {exception}", exc_info=True)
        return {"error": "Internal server error"}, 500

    def _setup_routes(self):
        @self.flask_app.route('/')
        def serve_index():
            return self._serve_webui()

        @self.flask_app.route('/<path:filename>')
        def serve_static(filename):
            return self._serve_webui(filename)

        @self.flask_app.route('/api/<endpoint>', methods=['GET', 'POST'])
        def handle_api(endpoint):
            try:
                method = request.method.lower()
                handler = getattr(self.rest_api, f'handle_{method}_{endpoint}', None)
                if not handler:
                    return {"error": "Not found"}, 404
                result = handler(request.get_json())
                return result
            except Exception as e:
                return self.unhandled_exception(e)

        @self.flask_app.errorhandler(Exception)
        def global_error_handler(exception):
            return self.unhandled_exception(exception)
