from flask import Flask, request, jsonify, abort
import json
import os

app = Flask(__name__)
COLORS_FILE = 'colors.json'

def load_colors():
    if not os.path.exists(COLORS_FILE):
        return {}
    with open(COLORS_FILE, 'r') as f:
        return json.load(f)

def save_colors(colors):
    with open(COLORS_FILE, 'w') as f:
        json.dump(colors, f, indent=4)

@app.route('/colors', methods=['GET'])
def get_colors():
    colors = load_colors()
    return jsonify(colors), 200

@app.route('/colors/<name>', methods=['GET'])
def get_color(name):
    colors = load_colors()
    color = colors.get(name)
    if color is None:
        abort(404, description="Color not found")
    return jsonify({name: color}), 200

@app.route('/colors', methods=['POST'])
def create_color():
    data = request.get_json()
    if not data or 'name' not in data or 'value' not in data:
        abort(400, description="Missing 'name' or 'value'")
    name = data['name']
    value = data['value']
    colors = load_colors()
    if name in colors:
        abort(409, description="Color already exists")
    colors[name] = value
    save_colors(colors)
    return jsonify({name: value}), 201

@app.route('/colors/<name>', methods=['PUT'])
def update_color(name):
    data = request.get_json()
    if not data:
        abort(400, description="No data provided")
    value = data.get('value')
    if value is None:
        abort(400, description="Missing 'value'")
    colors = load_colors()
    if name not in colors:
        abort(404, description="Color not found")
    colors[name] = value
    save_colors(colors)
    return jsonify({name: value}), 200

@app.route('/colors/<name>', methods=['DELETE'])
def delete_color(name):
    colors = load_colors()
    if name not in colors:
        abort(404, description="Color not found")
    del colors[name]
    save_colors(colors)
    return jsonify({name: "deleted"}), 200

if __name__ == '__main__':
    app.run(debug=True)
