                content_type = request.headers.get('Content-Type')
                input_data = request.get_json() if 'application/json' in content_type else request.data

                result = self._process_computation(input_data)

                return Response(json.dumps(result), status=200, mimetype='application/json')
            except Exception as e:
                return self._handle_error(e)

        self.app.register_blueprint(api)

        self._setup_logging(logfile, loglevel, log_exception_formatter)

    def _setup_logging(self, logfile, loglevel, formatter):
        numeric_level = getattr(logging, loglevel.upper(), None)
        if not isinstance(numeric_level, int):
            raise ValueError(f'Invalid log level: {loglevel}')
        logging.basicConfig(stream=logfile, level=numeric_level)

    def _process_computation(self, data):
        return {"status": "success", "data": data}

    def _handle_error(self, error):
        logging.exception("An error occurred")
        return Response(json.dumps({"error": str(error)}), status=500, mimetype='application/json')

    def run(self, port=6363, retry=False, **kwargs):
        self.port = port
        try:
            self.app.run(port=port, **kwargs)
        except Exception as e:
            if retry:
                new_port = port + 1
                print(f"Port {port} busy. Retrying on port {new_port}")
                self.run(port=new_port, retry=False, **kwargs)
            else:
                raise e
