import os
import threading
import time
import csv
import json
from io import StringIO
from flask import Flask, request, jsonify, send_file, Response
from dnstwist import scan

class DNSTwistAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self.sessions = []
        self._setup_routes()
        self._start_janitor()

    def _setup_routes(self):
        self.app.route('/', methods=['GET'])(self.root)
        self.app.route('/api/scan', methods=['POST'])(self.api_scan)
        self.app.route('/api/status/<sid>', methods=['GET'])(self.api_status)
        self.app.route('/api/domains/<sid>', methods=['GET'])(self.api_domains)
        self.app.route('/api/csv/<sid>', methods=['GET'])(self.api_csv)
        self.app.route('/api/json/<sid>', methods=['GET'])(self.api_json)
        self.app.route('/api/list/<sid>', methods=['GET'])(self.api_list)
        self.app.route('/api/stop/<sid>', methods=['POST'])(self.api_stop)

    def _start_janitor(self):
        janitor_thread = threading.Thread(target=self.janitor, daemon=True)
        janitor_thread.start()

    def janitor(self):
        while True:
            now = time.time()
            for session in list(self.sessions):
                if session['status'] == 'completed' or (session['last_active'] + 3600 < now):
                    self.sessions.remove(session)
            time.sleep(60)

    def root(self):
        return "DNSTwist API is running. Use /api/scan to start a scan."

    def api_scan(self):
        domain = request.json.get('domain')
        sid = os.urandom(16).hex()
        scanner = scan.Scan(domain, output='json', threads=20)
        scanner.start()
        session = {
            'sid': sid,
            'domain': domain,
            'status': 'running',
            'results': [],
            'last_active': time.time()
        }
        self.sessions.append(session)

        def background_scanner(scanner, sid):
            for result in scanner.results():
                for session in self.sessions:
                    if session['sid'] == sid:
                        session['results'].append(result)
                        session['last_active'] = time.time()
                        break
            for session in self.sessions:
                if session['sid'] == sid:
                    session['status'] = 'completed'
                    session['last_active'] = time.time()
                    break

        scanner_thread = threading.Thread(target=background_scanner, args=(scanner, sid), daemon=True)
        scanner_thread.start()
        return jsonify({'sid': sid}), 201

    def api_status(self, sid):
        for session in self.sessions:
            if session['sid'] == sid:
                session['last_active'] = time.time()
                return jsonify({
                    'status': session['status'],
                    'results_count': len(session['results'])
                })
        return jsonify({'error': 'Session not found'}), 404

    def api_domains(self, sid):
        for session in self.sessions:
            if session['sid'] == sid:
                session['last_active'] = time.time()
                return jsonify(session['results'])
        return jsonify({'error': 'Session not found'}), 404

    def api_csv(self, sid):
        for session in self.sessions:
            if session['sid'] == sid:
                session['last_active'] = time.time()
                si = StringIO()
                cw = csv.writer(si)
                cw.writerow(['domain', 'reason', 'type', 'ip', 'mx', 'txt', 'soa', 'ns', 'registrar', 'whois', 'dmarc'])
                for result in session['results']:
                    cw.writerow([
                        result['domain'], result['reason'], result['type'],
                        result['ip'], result['mx'], result['txt'], result['soa'],
                        result['ns'], result['registrar'], result['whois'], result['dmarc']
                    ])
                output = si.getvalue()
                return output, 200, {'Content-Type': 'text/csv'}
        return jsonify({'error': 'Session not found'}), 404

    def api_json(self, sid):
        for session in self.sessions:
            if session['sid'] == sid:
                session['last_active'] = time.time()
                return jsonify(session['results']), 200
        return jsonify({'error': 'Session not found'}), 404

    def api_list(self, sid):
        for session in self.sessions:
            if session['sid'] == sid:
                session['last_active'] = time.time()
                lines = [result['domain'] for result in session['results']]
                return '\n'.join(lines), 200, {'Content-Type': 'text/plain'}
        return jsonify({'error': 'Session not found'}), 404

    def api_stop(self, sid):
        for session in self.sessions:
            if session['sid'] == sid:
                session['status'] = 'stopped'
